float rand2d (vec2 ipos) {
    return pow(fract(sin(dot(ipos,vec2 (12559,1.)*100000000. ))),5.); 
    }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // vec2 uv = -1. + 2. * v_texcoord;
   vec2 uv = gl_FragCoord.xy / iResolution.xy;

   uv.y *= 50.;
   vec2 ipos = floor(uv);
   vec2 fpos = fract(uv);


   vec2 offset= fpos + (mod(sqrt(rand2d(ipos)) *iTime,8.))  ;

   float valuetest =  rand2d( rand2d(ipos) * offset);

   vec3 color= vec3 (valuetest);

   fragColor = vec4(color,
        1.0);
}
