void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-.5;

    uv=uv/(iTime)*100.0;

    uv+=(iMouse.xy-iResolution.xy/2.0)/100.0;

    vec3 iro = vec3(uv.xy,0);
	float sq=0.0;
    float sq2=0.0;
    while (iro.z < 1.0){
        sq=iro.x*iro.x;
        sq2=iro.y*iro.y;
        if (sq+sq2>10.0){
            break;}
        iro.x=sq-sq2+uv.x;
        iro.y=2.0*iro.x*iro.y+uv.y;
        iro.z+=.1;
    }

    // Output to screen
    fragColor = vec4(cos(sq),cos(sq2),iro.z,1.0);
}
