#define CENTER (iResolution.xy / 2.0)
#define PI 3.1415
#define TAU (2.0 * PI)

//Convert rectangular to polar coordinates
vec2 rect_to_polar(vec2 rect) {
    float r = length(rect);
    float theta = atan(rect.y, rect.x);

    //Fix the coordinates so they go from 0 to 2PI
    //instead of -PI to PI. These two lines are optional
    if (theta < 0.0)
        theta += TAU;

    return vec2(r, theta);
}

vec2 normalized_polar(vec2 coord) {
    //Centered UV coordinates accounting for aspect ratio
    vec2 uv = (coord - CENTER ) / iResolution.y;

    //Convert to polar. Normalize the angle component by
    //dividing by a full circle.
    vec2 polar = rect_to_polar(uv);
    polar.y /= TAU;

    return polar;
}

float Hash21(vec2 p) {
    p = fract(p*vec2(234.34, 435.35));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
 	vec2 st = fragCoord/iResolution.xy;

    vec2 uv = normalized_polar(fragCoord);


    float gp = uv.y;
    float gx = uv.x;


    uv.x = -pow(uv.x,.1);
    uv.x = uv.x + iTime/10.;
//    uv.x = mod(uv.x, 1.);

    vec3 col = vec3(0,0,0);

//  col.x = sin(uv.x*100.);
	col.x = -3.+sin(uv.x*100.-(gp*TAU))*10.;
    col.x = clamp(col.x, 0., 1.);
    col.x = min(col.x, gx+.01);

    col.y = -2.+sin(uv.y*TAU*8.+iTime)*25.;
    col.y = clamp(col.y, 0., 1.);
    col.y = min(col.y, gx+.01);

// truchet
    uv.y -= iTime*.03;
    uv *= 55.;
    //uv.y += iTime*3.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float n = Hash21(id);

    float width = .25;

    if(n<.5) gv.x *= -1.;
    float d = abs(abs(gv.x+gv.y)-.5);
    float mask = smoothstep(.01, -.01, d-width);


    //col += n;
    col.x -= mask;
    //mask = mask*-1.;
	col.y *= mask-col.y;


    // Output to screen
    fragColor = vec4(col,1.0);
}
