float Lookup(vec2 texCoord, vec2 offset) {
    float delta = sin(iTime * 2.5);
    vec2 uv = (texCoord.xy + offset * delta) / iResolution.xy;
    vec4 color = texture(iChannel0, uv.xy);
    return 0.2 * color.r + 0.7 * color.g + 0.1 * color.b;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float x = 0.0;
    float y = 0.0;
    vec2 uv = fragCoord.xy;
    vec4 color = texture(iChannel0, uv / iResolution.xy);
    x += -1.0 * Lookup(uv, vec2(-1.0, -1.0));
    x += -2.0 * Lookup(uv, vec2(-1.0,  0.0));
    x += -1.0 * Lookup(uv, vec2(-1.0,  1.0));
    x +=  1.0 * Lookup(uv, vec2( 1.0, -1.0));
    x +=  2.0 * Lookup(uv, vec2( 1.0,  0.0));
    x +=  1.0 * Lookup(uv, vec2( 1.0,  1.0));
    y += -1.0 * Lookup(uv, vec2(-1.0, -1.0));
    y += -2.0 * Lookup(uv, vec2( 0.0, -1.0));
    y += -1.0 * Lookup(uv, vec2( 1.0, -1.0));
    y +=  1.0 * Lookup(uv, vec2(-1.0,  1.0));
    y +=  2.0 * Lookup(uv, vec2( 0.0,  1.0));
    y +=  1.0 * Lookup(uv, vec2( 1.0,  1.0));


    float square = x * x + y * y;
    color += vec4(square, square * 0.25, 0.0, 1.0);
    fragColor = color;
}
