void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(0.0);
	float f = 0.0;

    vec2 pos = vec2(0.5)-uv;
    float a = atan(pos.x,pos.y);
    float r = length(pos)*10.;

    f = 3.+cos(a*abs(sin(iTime*.2))*5.)*sin(iTime+a);
    f += cos(r*abs(sin(iTime*.1))*10.)*cos(iTime+a);
    f += mod(f, 1.5);

    color.r = 1.-smoothstep(f,f+0.0,r);
    color.g = 1.-smoothstep(f,f+abs(cos(a)),r);
    color.b = 1.-smoothstep(f,f+abs(sin(a)),r);

    // Output to screen
    fragColor = vec4(color,1.0);
}
