#define PI 3.14159265359
#define SCALE 20.0


mat2 rotate2d(float angle){
	float s = sin(angle);
    float c = cos(angle);

    return mat2(c, -s, s, c);
}

vec2 polar(vec2 uv){
	return vec2(atan(uv.x, uv.y), length(uv));
}

float randomDither(float v, vec2 uv){
	float r = fract(sin(dot(uv, vec2(8293.8214, 5812.8372))) * 1242.10);
	return step(r, v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    float t = iTime * .05;
    vec2 p = polar(uv);
    float l = sin(p.x+t)*.5+.7;

    p.x *= 6./ ( sqrt(2.) * PI*2. );
    if(p.y > .7){
    	p.y -= .7;
        t = -t;
    }
	p.y = (log2(p.y) * .1) - t;
    p *= rotate2d(PI/4.0);


    vec2 fp = fract(p * SCALE) - .5;

    vec2 rp = fp * rotate2d(-PI/4.0);

    float c = fract(rp.x) * (rp.y + .3) * l;
    c = mix(randomDither(c, fragCoord), c, .9);

    fragColor = vec4(vec3(c),1.0);
}
