void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
	xy.x = xy.x / iResolution.x;
  	xy.y = xy.y / iResolution.y;

    vec4 gradient = vec4(0,0,0,1.0);

    if (xy.x <= 0.25) {
     gradient.r = xy.x * 4.0;   
    } else if (xy.x <= 0.50) {
        gradient.r = 1.0;
        gradient.g = (xy.x - 0.25) * 4.0;
    } else if (xy.x <= 0.75) {
     	gradient.r = 1.0 - ((xy.x - 0.50) * 4.0);
        gradient.g = 1.0;
        gradient.b = (xy.x - 0.50) * 4.0;
    } else {
        gradient.r = (xy.x - 0.75) * 4.0;
        gradient.g = 1.0 - ((xy.x - 0.75) * 4.0);
        gradient.b = 1.0;
    }
   	fragColor = gradient;


}
