#define AMPLITUDE .5
#define ZOOM 4.5
#define ZOOM_MIN .5
#define ZOOM_SPEED .5
#define ROTATE_SPEED .1
#define FADE_SPEED .25

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5 * iResolution.xy) / min(iResolution.x, iResolution.y);
    float angle = iTime * ROTATE_SPEED;
    float mixLevel = .5 + .5 * sin(iTime * FADE_SPEED);

    // Sine wave
    uv.y = uv.y + AMPLITUDE * (sin(iTime) * sin(iTime + uv.x) - .5);

    // Zoom
    uv = uv * (ZOOM * vec2(.5 + .5 * sin(iTime * ZOOM_SPEED)) + ZOOM_MIN);

    // Rotate
    uv = vec2(uv.x * cos(angle) - uv.y * sin(angle),
              uv.x * sin(angle) + uv.y * cos(angle));

    if(length(uv) < 1.){
        // I'm different
        mixLevel = 1. - mixLevel;
    }
    fragColor = mix(texture(iChannel0, uv), texture(iChannel1, uv),
                    mixLevel);
}
