//Bottom left for all rectangles
vec2 houseBody = vec2(0.3, 0.0);
vec2 chimneyBody = vec2(0.7, 0.6);
vec2 window1 = vec2(0.4, 0.3);
vec2 window2 = vec2(0.6, 0.3);

//Height and width for all
//HouseBody
vec2 bMeasurements = vec2(0.5, 0.6);
vec2 cMeasurements = vec2(0.1, 0.3);
vec2 wMeasurements = vec2(0.1, 0.2);

//Colors for all
vec3 bgColor = vec3(1.0, 1.0, 1.0);
vec3 bColor = vec3(1.0, 0.0, 0.0);
vec3 cColor = vec3(0.0, 0.0, 0.0);
vec3 wColor = vec3(0.0, 0.0, 1.0);

//On and off switches for each layer
float bgOn = 1.0;
float houseOn = 1.0;
float chimneyOn = 1.0;
float windowsOn = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	//uv.y *= iResolution.y / iResolution.x;
    //houseBody.y *= iResolution.y / iResolution.x;
    //chimneyBody.y *= iResolution.y / iResolution.x;
    //window1.y *= iResolution.y / iResolution.x;
    //window2.y *= iResolution.y / iResolution.x;

    vec4 layer1 = vec4(bgColor, 1.0);
    vec4 layer2 = vec4(bColor, 0.0);
    vec4 layer3 = vec4(cColor, 0.0);
    vec4 layer4 = vec4(wColor, 0.0);

    layer2.a = (step(houseBody.x, uv.x) - step(houseBody.x + bMeasurements.x, uv.x)) *
       (step(houseBody.y, uv.y) - step(houseBody.y + bMeasurements.y, uv.y));
    layer3.a = (step(chimneyBody.x, uv.x) - step(chimneyBody.x + cMeasurements.x, uv.x)) *
       (step(chimneyBody.y, uv.y) - step(chimneyBody.y + cMeasurements.y, uv.y));
    layer4.a = (step(window1.x, uv.x) - step(window1.x + wMeasurements.x, uv.x)) *
       (step(window1.y, uv.y) - step(window1.y + wMeasurements.y, uv.y)) + 
        (step(window2.x, uv.x) - step(window2.x + wMeasurements.x, uv.x)) *
       (step(window2.y, uv.y) - step(window2.y + wMeasurements.y, uv.y));

    //Turn off layers as needed
    layer1.a *= bgOn;
    layer2.a *= houseOn;
    layer3.a *= chimneyOn;
    layer4.a *= windowsOn;


    // Output to screen
    vec4 combined = mix(layer1, layer2, layer2.a);
    combined = mix(combined, layer3, layer3.a);
    combined = mix(combined, layer4, layer4.a);
    fragColor = combined;
}
