float Spikes(float angle, float armSpeed, float armNum, float armWidth, float size)
{
    float spikes =  1.0 / (abs(cos(angle * 500.0 + (iTime * armSpeed)) 
                        * sin(angle * armNum + iTime )))
        											* armWidth +      size;

    return spikes;

}

float Gear(float angle, float armCount, float rotationSpeed, float armLength, float gearSize, float direction)
{
    float  radius = smoothstep(-1.0 ,  1.0,  
                      cos(angle * armCount + (direction * iTime * rotationSpeed))) 
        * armLength  + gearSize;

    return radius;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    vec2 ouv = uv;
    uv.x *= ratio;

    //initialize position
    vec2 shapePos = vec2((0.5 * ratio) , 0.5);
    float dist = distance(shapePos , uv) * 2.50;

    vec3 black = vec3(0.0 , 0.0, 0.0);
    vec4 mouse = iMouse/iResolution.xyxy;

    uv *= 3.0;
    uv = fract(abs(sin(uv * 2.0)));


    uv -= shapePos;

    float angle = atan(uv.x , uv.y);
    float radius;

    float flakes = Spikes(angle, 1.0, 6.0, 0.5, 0.07);
    //snoflakes
    //radius = 1.0/abs(cos(angle * 500.0 + iTime) 
    //             * sin(angle * 6.0 + iTime)) 
      //  			* 0.5 + 0.07;
    float value =  step(flakes  , dist * 0.9 );

    uv = ouv;
    uv.x *= ratio;
    uv -= shapePos;
    angle = atan(uv.x, uv.y);

    float direction = -1.0;
    if(mouse.y < 0.6)
    {
        direction *= -1.0;
    }

  	radius = smoothstep(-0.75 , 0.0, cos(angle * (direction * 3.0) + iTime * 2.0)  ) 
        				* 0.9 + 0.2;
  	value += step(radius, dist * 0.3);


    // Output to screen
    fragColor = vec4(value  * vec4(vec3(0.3, 0.9, 1.0) , 1.0));
}
