//Colors for checkerboard
//vec4 white = vec4(0.0, 0.0, 0.0, 1.0);
//vec4 black = vec4(1.0, 1.0, 1.0, 1.0);

//Used to darken up the wood+London squares
//They're a little too bright for my liking otherwise
vec4 dark_tint = vec4(0.75);

float num_rows = 10.0;
vec4 col;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
// Normalized pixel coordinates (from 0 to 1)
vec2 uv = fragCoord/iResolution.xy;

//Loading in textures
vec4 white = texture(iChannel0, uv);
vec4 black = texture(iChannel1, uv);

//Flooring x & y sizes
float x = floor(uv.x * num_rows);
float y = floor(uv.y * num_rows);

//Determining output    
if(mod(x+y, 2.0) == 0.0)
    col = white;
else
	col = (black + white) * dark_tint;

// Output to screen
fragColor = col;
}
