void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float speed = 1.;
    float scale = 0.002;
    vec2 p = (fragCoord.xy/iResolution.xy- vec2(0.5))*2.0;
    p.x *= iResolution.x/iResolution.y;
    p *= 0.5;
    p.x += iTime/2.;
    //vec2 p = fragCoord * scale;   
    for(int i=1; i<10; i++){
        p.x+=0.3/float(i)*sin(float(i)*3.*p.y+iTime*speed*1.);//+iMouse.x/1000.;
        p.y+=0.3/float(i)*cos(float(i)*3.*p.x+iTime*speed*1.);//+iMouse.y/1000.;
    }
    //p.xy += iTime*10.;

    float t = iTime*1.0;
    float gbOff = p.x;
    float gOff = 0.0+p.y;
    float rOff = 0.0;
    float r=cos(p.x+p.y+1.+rOff)*.5+.5;
    float g=sin(p.x+p.y+1.+gbOff+gOff)*.5+.5;
    float b=(sin(p.x+p.y+gbOff)+cos(p.x+p.y+gbOff))*.3+.5;
    vec3 color = vec3(r,g,b);
    //vec3 color = vec3(p.x,p.y,0.0);
    fragColor = vec4(color,1);
}
