float borderSize = 3.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;  
	uv.y = 1. - uv.y;
    vec4 color = vec4(1, 0, 0, 1);
    vec4 bg = texture( iChannel0, uv);
    float ratio = iResolution.x / iResolution.y;
    vec4 bgColor =  texture( iChannel0, uv);

    // outer
                   // x1  y1   x2   y2
    vec4 rect = vec4(0.0, 0.0, 1, 1);
    vec2 hv = step(rect.xy, uv) * step(uv, rect.zw);
    float onOff = hv.x * hv.y;
    vec4 col1 = mix(vec4(0), vec4(0,0,0,0), onOff); // inner is visible

    // inner
    float	thickness1 = (borderSize/iResolution.x) * ratio;
    float	thickness2 = (borderSize/iResolution.y) * ratio;
    vec4 inner = vec4(rect[0] + thickness1, 
                      rect[1] + thickness2, 
                      rect[2] - thickness1, 
                      rect[3] - thickness2);
    hv = step(inner.xy, uv) * step(uv, inner.zw);
    onOff = hv.x * hv.y;
    vec4 col2 = mix(vec4(0), vec4(0,0,0,1), onOff);
    bgColor = mix(col1, bgColor, onOff);

    fragColor = bgColor + (col2 * col1);
}
