const float speed = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  	vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
 	uv.x *= iResolution.x / iResolution.y;

    float t = iTime*speed;

	float modu = 0.5 + 0.5*sin(5.0*(length(uv*3.0) - t));

	float fade = smoothstep(0.99, 0.0, fract(length(uv)*0.99)*2.0);

	fragColor = vec4(vec3(modu*fade,modu*fade,modu*fade), 1.0);
}
