{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 blendedCol = texture(iChannel0, uv).rgb;
    float shift = (1.0f/255.0f);

    if(iFrame % 300 > 100)
    {
    int offset =   (int(fragCoord.x) % 2) + (1-(int(fragCoord.y) % 2))*2;
    blendedCol += (float((  iFrame+ offset)% 4)/4.0f)* shift;
    }
    fragColor = vec4(blendedCol,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float locTime = iTime * 0.15;
    vec2 dotPos = vec2(cos(locTime), sin(locTime))*.5 + vec2(0.5);

    // Time varying pixel color
    float dist = (length( dotPos-uv));

    vec3 colA = vec3(0.35,0.27,0.179);
    vec3 colB = vec3(0.195,0.144,0.113);
    // Output to screen
    vec3 blendedCol = mix( colA, colB, dist);
    fragColor = vec4(blendedCol,1.0);
}
