void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = uv - vec2(0.5,0.5);
    vec2 z = p * 1.5;
    vec2 c = vec2(-0.745  + (sin(iTime*0.05)), 0.19 + (sin(iTime*0.05)));
    float t = 0.0;
    vec2 bo = vec2(9999.0,9999.0);
    float bl = 99999.0;
    vec2 wo = vec2(0.0,0.0);
    float wl = 0.0;
    float it = 0.0;
    float os = 0.0;
    for (int i = 0; i < 64; i ++)
    {
     	vec2 nz = vec2(z.x*z.x - z.y*z.y, (1.5 - cos(iTime * 0.033)) * z.x * z.y) +c;
        vec2 no = nz - p;
        float nl = length(no);
        os += nl;
        it += 1.0;
        if (nl > wl)
        {
        	wo = no;
            wl = nl;

        }
        if (nl < bl)
        {
        	bo = no;
            bl = nl;

        }

        float m2 = dot(z.x,z.x);
        if (m2 > 4.0)
        {
           // bl = 1.0;
        	break;   
        }
        z = nz;
        t += 1.0/63.0;
    }

	vec3 tintb = vec3(0.3,0.6,0.8);
    vec3 tintr = vec3(0.8,0.4,0.1);
    float avgo = os/it;

    vec3 col = mix(mix(vec3(t) , tintb, dot(bo,wo)+avgo) , mix(vec3(t) , tintr, sqrt(avgo)) , sqrt(bl));

    // Output to screen
    fragColor = vec4(col,1.0);
}
