void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    //fragColor = vec4(col,1.0);

    vec4 timePix = texture(iChannel1, uv);


    float timeThreshold = (timePix.r + timePix.g + timePix.b) / 2.5;

    vec4 color;// = texture(iChannel0, uv);

    float currentTime = abs(sin(mod(iTime / 3.0, 1.0) * 3.0));
    if( currentTime > timeThreshold )
    {
        color = timePix - color;
        //color.r = timeThreshold;

        //float temp = color.r;
        //color.r = color.b;
        //color.b = temp;
    }

    color.r -= currentTime - timeThreshold;
    color.g -= currentTime - timeThreshold;
    color.b -= currentTime - timeThreshold;

    fragColor = color;
}
