void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

	// Get luma
    vec4 tex = texture(iChannel0, uv);
    float l = dot(tex.rgb, vec3(0.299, 0.587, 0.114));

    // Sample the gradient
    vec4 o = texture(iChannel1, vec2(l));

    fragColor = o;

    // draw gradient along the top
    if(uv.y > 0.9){
     fragColor = texture(iChannel1, uv);   
    }

}

vec3 gradient(vec3 c1, vec3 c2, vec3 c3, float grad){
    vec3 o = mix(c1, c3, grad);
    float u = 1.0 - abs(grad * 2.0 - 1.0);

    // an alternate gradient
    //u = sin(grad * 3.1415972);
    //u = pow(u, 4.0);

    o = mix(o, c2, u);
    return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 c1 = vec3(1.0,0.0,0.0);
    vec3 c2 = vec3(0.0,1.0,0.0);
    vec3 c3 = vec3(0.0,0.0,1.0);

    fragColor = vec4(gradient(c1, c2, c3, uv.x), 1.0);
}
