void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy);

    vec4 textureA = texture(iChannel0, uv);
    vec4 textureB = texture(iChannel1, uv);

   	float displaceAmount = .5;

    // float blend = max(0., (min(1., sin(iTime) + 0.5)))
    float blend = clamp(sin(iTime) + 0.5, 0., 1.);
    float blend2 = 1.-blend;

	float t1 = ((textureB.r * displaceAmount) * blend);
    float t2 = ((textureA.r * displaceAmount) * blend2);


    vec4 imageA = texture(iChannel1, vec2(uv.x, uv.y-t1))* blend2;
    vec4 imageB = texture(iChannel0, vec2(uv.x, uv.y+t2))* blend;

    fragColor = imageA.bbra * blend + 
                imageA * blend2 +
                imageB.bbra * blend2 + 
                imageB * blend;
}
