const int AMOUNT = 9;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 p = 20.0 * (fragCoord - iResolution.xy / 2.0) / min(iResolution.y, iResolution.x);

  float len;

  for(int i = 0; i < AMOUNT; i++){
    len = length(vec2(p.x, p.y));

    p.x = p.x - cos(p.y + sin(len)) + cos(iTime / 5.0);
    p.y = p.y + sin(p.x + cos(len)) + sin(iTime / 6.0);
  }

  vec3 col = vec3(0.2, cos(len), sin(len*2.6));

  fragColor = vec4(col,1.0);
}
