#define BLUE vec3(59.,59.,109.)/255.
#define RED vec3(179.,33.,52.)/255.
#define PI 3.1415926535

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord / iResolution.y;

    float d = p.y + sin(p.x*10.0 - iTime*3.0)/20.0*p.x;

    vec3 str = mix(RED,vec3(1.0,1.0,1.0), float(int(d*13.)%2));

    vec2 sp = vec2(p.x/0.65*26./24.-1./26.,(1.-d)/7.*13.*26./24.-1./26.);
    vec2 np = fract(vec2(sp.x*11.,sp.y*9.))-vec2(0.5,0.5);


    float na = atan(np.y, np.x) / PI + 1.5;
    na = na/2.0;
	float nd = length(np);

    float nlen = 0.5-abs(fract(na*5.0)-0.5);

    float nx = 0.2*0.809016994/sin(0.9424777961-mix(0.,0.6283185307,nlen*2.));
    nx = step(nd,nx);

    int ch = (int(sp.x*11.)+int(sp.y*9.))%2;

    vec3 sta = (ch==0 && nx>0. && sp.x>0. && sp.x<1. && sp.y>0. && sp.y<1.) ? vec3(1.0) : BLUE;

    vec3 col = (d<0.0 || d>1.0) ? vec3(0.0,0.0,0.0) : (d > 6./13. && p.x < 0.65) ? sta : str;

    fragColor = vec4(col,1.0);
}
