void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float amp = 0.1; //振幅
    float freq = 6.0; //周波数
    float thickness = 0.05 / 2.0; //線の太さ
    float smoothing = 4.0 / iResolution.x; //画面解像度に応じたアンチエイリアス

    //サインカーブをtimeでmix
    //float y = mix(amp * sin(uv.x * freq + iTime), amp * cos(uv.x * freq * 2. + iTime),  sin(iTime));
    float wave = uv.x * freq + iTime;
    //wave01
    float y = amp * (wave - floor(wave)) + amp * acos(cos(2.0 * wave)) + amp  - 0.3;
    //wave02
    float s = amp * sin(wave);
    //mix
    y = mix(y,s, sin(iTime));

    vec3 linecolor = vec3(uv.x * sin(iTime), uv.y * sin(iTime), 0.9);
    vec3 coltop = vec3(smoothstep(0.0, smoothing, uv.y - y - 0.5 + thickness)) * linecolor;
    vec3 colbottom = vec3(1. - smoothstep(0.0, smoothing, uv.y - y - 0.5 - thickness));
    vec3 col = coltop * colbottom;

    fragColor = vec4(col,1.0);
}
