Main {
    Q = D(U);
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texelFetch(iChannel3,ivec2(U),0)
#define Main void mainImage (out vec4 Q, vec2 U) 
#define Init if (iFrame < 1) 
#define Border if (U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.)
#define T(U) A((U)-dt*A(U).xy)
#define NeighborhoodT vec4 n = T(U+vec2(0,1)), e = T(U+vec2(1,0)), s = T(U-vec2(0,1)), w = T(U-vec2(1,0)), m = 0.25*(n+e+s+w);
#define Neighborhood vec4 n = A(U+vec2(0,1)), e = A(U+vec2(1,0)), s = A(U-vec2(0,1)), w = A(U-vec2(1,0)), m = 0.25*(n+e+s+w);
#define grd 0.25*vec2(e.z-w.z,n.z-s.z)
#define div 0.25*(e.x-w.x+n.y-s.y)
float pack (vec2 u) {
	return fract(u.x)+ceil(u.y*1e3);
}
vec2 unpack (float u) {
	return vec2(fract(u),ceil(u)/1e3);
}
#define I 16
//Dave H :
vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);

}

float f (vec2 U) {
    vec4 c = D(U);
	return length(c.xyz);
}
vec2 grad (vec2 U) {
	float 
        n = f(U+vec2(0,1)),
        e = f(U+vec2(1,0)),
        s = f(U-vec2(0,1)),
        w = f(U-vec2(1,0));
    return vec2(e-w,n-s);
}
vec2 convo (vec2 U) {
	vec2 r = vec2(0);
    float d = 0.;
    for (int x = -2; x<=2; x++) {
        for (int y = -2; y<=2; y++) {
            vec2 v = vec2(x,y);
            r += v*f(v+U);
            d += dot(v,grad(v+U));
        }
    }
    r/=25.;d/=25.;
    return vec2(2.*dot(r,r),0.5*d+0.5);
}

Main {
    Q.xy = convo(U);
    Q.xy = clamp(Q.xy,0.,1.);
    vec4 c = D(U);
    Q.zw = vec2(c.x,pack(c.yz));

}

void X (inout vec4 Q, vec2 U, vec4 n) {
	if (length((n.xy)*R-U)<length((Q.xy)*R-U))
        Q = n;
}
Main {
    if (iFrame%I==0) {
        Q = A(U);
    } else {
    	float k = exp2(float(I-1-(iFrame%I)));
        X(Q,U,B(U+vec2(0,k)));
        X(Q,U,B(U+vec2(k,0)));
        X(Q,U,B(U-vec2(0,k)));
        X(Q,U,B(U-vec2(k,0)));
    }
}

void X (inout vec4 Q, vec2 U, vec4 n) {
	if (length((n.xy)*R-U)<length((Q.xy)*R-U))
        Q = n;
}
Main {
    Q = B(U);
    X(Q,U,C(U+vec2(0,1)));
    X(Q,U,C(U+vec2(1,0)));
    X(Q,U,C(U-vec2(0,1)));
    X(Q,U,C(U-vec2(1,0)));

    X(Q,U,C(U+vec2(1,1)));
    X(Q,U,C(U+vec2(1,-1)));
    X(Q,U,C(U-vec2(1,1)));
    X(Q,U,C(U-vec2(1,-1)));


    X(Q,U,C(U+vec2(0,2)));
    X(Q,U,C(U+vec2(2,0)));
    X(Q,U,C(U-vec2(0,2)));
    X(Q,U,C(U-vec2(2,0)));


}

float f (vec2 U) {
	return length(A(U).xyz);
}
vec2 grad (vec2 U) {
	float 
        n = f(U+vec2(0,1)),
        e = f(U+vec2(1,0)),
        s = f(U-vec2(0,1)),
        w = f(U-vec2(1,0));
    return vec2(e-w,n-s);
}
vec2 convo (vec2 U) {
	vec2 r = vec2(0);
    float d = 0.;
    for (int x = -2; x<=2; x++) {
        for (int y = -2; y<=2; y++) {
            vec2 v = vec2(x,y);
            r += v*f(v+U);
            d += dot(v,grad(v+U));
        }
    }
    r/=25.;d/=25.;
    return vec2(2.*dot(r,r),.5*d+0.5);
}
Main {
    U -= 0.5*R;
    float a = -1e-3-2e-3*cos(length(U)*.01);
    U *= (1.-.005*exp(-4e-3*length(U)))*mat2(cos(a),sin(-a),sin(a),cos(a));
    U += 0.5*R;
	Q = A(U);
    Neighborhood;
    vec2 u = convo(U);
    vec4 c = C(u*R);
    c.x = c.z;
    c.yz = unpack(c.w);
    if (length(c.xyz)>0.0&&length(c.xyz)<1.4)
	Q = mix(Q,c,0.01);
    if (iFrame < 100||iMouse.z>0.) {
        Q = C(U);

    	Q=vec4(Q.z,unpack(Q.w),1);//*smoothstep(2.,1.,length(U-Q.xy*R));

        Q = clamp(Q,0.,1.);

    }
}
