// c is the sin/cos of the half-angle.
float sdClockPlane( in vec2 p, in vec2 c )
{
    vec2 p90 = vec2(p.y,-p.x);
    vec2 c90 = vec2(c.y,-c.x);
    p = vec2(abs(dot(p,c)),dot(p90,c));
	float m = length(p - c*max(dot(p,c),0.0));
    return m*sign(dot(c90,p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    // animation
    float t =            3.14*(0.5+0.5*cos(iTime*0.52));
    vec2  w = vec2(0.50,0.25)*(0.5+0.5*cos(iTime*vec2(1.1,1.3)+vec2(0.0,2.0)));

    // distance
    float len = sdClockPlane(uv,vec2(sin(t),cos(t)));

    // coloring
    vec3 col = vec3(1.0) - sign(len)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(len));
	col *= 0.8 + 0.2*cos(128.0*abs(len));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(len)) );

	fragColor = vec4(col, 1.0);
}
