void mainImage(out vec4 O, vec2 C) {
    O=vec4(0);
	for(float g,e,i;i++<50.;){
        vec3 p=g*vec3((C*2.-iResolution.xy)/iResolution.y,1.);
        p.z+=iTime;
        p.xy=vec2(mod(atan(p.x,p.y)+iTime*(mod(ceil(p.z),2.)-.5),.42)-.21,cos(length(p.xy)));
        p.x-=clamp(p.x,-.1,.1);
        g+=e=.7*length(vec2(length(p.xy),fract(p.z)-.5))-.06;
        e<.01?O.y+=8./i/i:g;
    }
}
