void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord)/iResolution.xy;
    // Time varying pixel color
    float time1 = iTime/2.0;
    float scale = 5.0;
    vec2 col = (uv.yx*scale*(1.0+sin(uv.x+time1/10.)/2.0));
    vec2 col2;
    for(float i = 1.0; i < 4.0; i++){
        uv += ceil(col+tan((col.x+col.y))/5.0);
        col -= sin(uv.x+time1/10.);
        col2 = (col2+col)/(i*i);
    }

    // Output to screen
    fragColor = vec4(col.y,col2.x,0.5,0.5);
}
