    return distance(p, c) - r;
}

float cylinder(vec3 p, float h, float r)
{
  vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(h, r);
  return min(max(d.x, d.y), 0.0) + length(max(d,0.0));
}

float ellipse(vec3 p, vec3 r)
{
  float k0 = length(p / r);
  float k1 = length(p / (r * r));
  return k0 * (k0 - 1.0) / k1;
}

float cone(vec3 p, float r1, float r2, float h)
{
  vec2 q = vec2(length(p.xz), p.y);

  float b = (r1 - r2) / h;
  float a = sqrt(1.0 - b * b);
  float k = dot(q, vec2(-b, a));

  if(k < 0.0) {
      return length(q) - r1;
  }
  if(k > a * h) {
      return length(q - vec2(0.0, h)) - r2;
  }

  return dot(q, vec2(a, b)) - r1;
}

float capsule(vec3 p, float h, float r)
{
  p.y -= clamp(p.y, 0.0, h);
  return length(p) - r;
}

float plants(in vec3 p)
{
    const vec3 c = vec3(3.5, 0., 0.);
    vec3 q = mod(p + 0.5 * c, c) - 0.5 * c;

    float d1 = cylinder(rotateZ(q, -PI / 2.), 2., 2.);
    vec3 p2 = rotateZ(q, PI / 2.2 + 0.2 * length(p - q));
    float d2 = ((sin(10. * p2.x) - 1.0) * 0.5) * ((sin(4. * p2.y) - 1.0) * 0.5)
        * ((sin(1.9 * p2.z) - 1.0) * 0.5);
    return d1 + 2. * d2;
}


