void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos)
{
    t = FLOOR_SDF;
    obj = FLOOR;

    float t2;

    if ((t2 = TREES_SDF) < t) {
        t = t2;
        obj = TREES;
    }

    if (pos.y < 1.9) {
        if ((t2 = PLANTS_SDF) < t) {
            t = t2;
            obj = PLANTS;
        }
    }
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeNormal(vec3 pos, vec3 lightPos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                            sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                            sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}


// fractal brownian motion
float random2D(vec2 p) {
  return fract(sin(dot(p, vec2(45.123, 23456.3459))) * 234.235);
}

float interpNoise2D(vec2 p) {
    float intX = floor(p.x);
    float fractX = fract(p.x);
    float intY = floor(p.y);
    float fractY = fract(p.y);

    float v1 = random2D(vec2(intX, intY));
    float v2 = random2D(vec2(intX + 1., intY));
    float v3 = random2D(vec2(intX, intY + 1.));
    float v4 = random2D(vec2(intX + 1., intY + 1.));

    float i1 = mix(v1, v2, fractX);
    float i2 = mix(v3, v4, fractX);
    return mix(i1, i2, fractY);
}


float fbm(vec2 p, float octaves) {
  float amp = 0.5;
  float freq = 8.0;
  float sum = 0.0;
  float maxSum = 0.0;
  for(float i = 0.0; i < 10.0; ++i) {
    if(i == octaves)
    break;
    maxSum += amp;
    sum += interpNoise2D(p * freq) * amp;
    amp *= 0.5;
    freq *= 2.0;
  }
  return sum / maxSum;
}

vec3 genFBMColor(float t) {
    vec3 a = vec3(0.1, 1.0, 0.5);
    vec3 b = vec3(1.0, 0.5, 0.5);
    vec3 c = vec3(0.9, 0.9, 1.0);
    vec3 d = vec3(0.0, 0.5, 0.66);

    return a + b * cos(2. * PI * (t * c * d));
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 light, vec3 view) {
    float t;
    switch(hitObj) {        
        case TREES:
        vec3 brown = 0.6 * vec3(0.9, 0.7, 0.62) * max (0.0, dot(n, light));
        brown += 0.3 * vec3(0.5, 0.48, 0.42);

        float fbm1 = fbm(vec2(interpNoise2D(vec2(p.x, p.y)), interpNoise2D(vec2(p.y, p.x))), 4.0);
        vec3 color1 = genFBMColor(fbm1) * max(0.0, dot(n, light));        
        return color1 * brown;
        break;

        case FLOOR:
        vec3 white = vec3(1.0, 1.0, 1.0) * max(0.0, dot(n, light));
        return white;
        break;

        case PLANTS:
        vec3 darkGreen = vec3(0.6, 0.9, 0.7) * max(0.0, dot(n, light));
        return darkGreen += 0.5 * vec3(0.5, 0.7, 0.5);
        break;

        case -1:
        return vec3(0.0);
        break;
    }
    return vec3(1.0);
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    if(t == -1.0) {
        return Intersection(t, skyColor(dir), vec3(eye + 1000.0 * dir), -1);
    }

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    return Intersection(t, surfaceColor, isect, hitObj);
}


