    Intersection aaIsects[4];
    vec3 dir, eye, ref;
    int idx = 0;
    for(float i = 0.0; i < 1.0; i += 0.5) {
        for(float j = 0.0; j < 1.0; j += 0.5) {
            raycast(uv2 + vec2(i, j) / iResolution.xy, dir, eye, ref);
            aaIsects[idx++] = sdf3D(dir, eye, LIGHT_POS);
        }
    }

    // set focal length and range
    float focalLength = 1.0;
	float focalRange = 5.0;

    float distAlongCamZ = abs(dot(normalize(ref - eye), (aaIsects[0].p - eye)));
    float dofZ = min(1.0, abs(distAlongCamZ - focalLength) / focalRange);
    dofZ = pow(dofZ, 0.5);
    vec3 avgColor = vec3(0.0);
    for(int i = 0; i < 4; ++i) {
        avgColor += aaIsects[i].color;
    }
    avgColor *= 0.25;
    int objHit = aaIsects[0].object;

    fragColor = vec4(avgColor * step(0.0, aaIsects[0].t), dofZ);

    float fogT = smoothstep(30.0, 50.0, distance(aaIsects[0].p, eye));
    fragColor.rgb = mix(fragColor.rgb, vec3(0.05, 0.08, 0.05), fogT);

}

