}

float bend(in vec3 p, float h, float r)
{
    const float k = -0.1;
    float c = cos(k * p.x);
    float s = sin(k * p.x);
    mat2  m = mat2(c, -s , s, c);
    vec3  q = vec3(m * p.xy, p.z);
    return cylinder(q, h, r);
}

float nose(in vec3 p, float h, float r)
{
    const float k = 0.4;
    float c = cos(k * p.y);
    float s = sin(k * p.y);
    mat2  m = mat2(c, -s , s, c);
    vec3  q = vec3(m * p.xy, p.z);
    return capsule(q, h, r);
}


// SDF REPETITION
float repeatMark(in vec3 p, float rotZ1, float rotZ2, float r1, float r2, float h)
{
    vec3 c = vec3(1.2, 0., 0.);
    vec3 q = mod(p + 0.5 * c, c) - 0.5 * c;
    vec3 ref = vec3(4.7, 0.5, 0.1);
    float refVar = abs(length(q - ref));
    vec3 offset = q;
    if (p.x > 2.3) {
        offset += vec3(0., length(q - p) * 0.1, -0.6);
    } else if (p.x > 1.7) {
        offset += vec3(0., length(q - p) * 0.1, -0.23);
    } else if (p.x < -1.7) {
        offset += vec3(0., length(q - p) * 0.1, -0.33);
    } else if (p.x > 0.5) {
        offset += vec3(0., length(q - p) * 0.1, -0.12);
    } else {
        offset += vec3(0., length(q - p) * 0.1, 0.035);
    }
    float lmark = cone(rotateZ(offset, rotZ1), r1, r2, h);
    float rmark = cone(rotateZ(offset, rotZ2), r1, r2, h);
    float mark = unionOp(lmark, rmark);
    return mark - 0.005 * refVar;
}

