void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos)
{
    t = FLOOR_SDF;
    obj = FLOOR;

    float t2;

    if (pos.x > 5.0 && pos.x < 8.5 && pos.y > -5. && pos.y < 1.9) {
        if ((t2 = SIGN_SDF) < t) {
        	t = t2;
        	obj = SIGN;
        }
    }

    if (pos.x > 5.0 && pos.x < 8.5 && pos.y >= 1.6 && pos.y < 4.0) {
        if ((t2 = SIGN_TOP) < t) {
            t = t2;
            obj = SIGN_POST;
        }
    }

    if (pos.x < -0.8 && pos.x > -9. && pos.y < 6.0 && pos.y > -5.0) {
        if ((t2 = TOTORO_SDF) < t) {
            t = t2;
            obj = TOTORO;
        }
        if (pos.x < -1.4 && pos.x > -8.2 && pos.y < 2.0 && pos.y > -4.5) {
            if ((t2 = TOTORO_MARKINGS) < t) {
                t = t2;
                obj = TOTORO_FUR;
            }            
        }
        if (pos.x < -3.3 && pos.x > -6.4 && pos.y > 2.4 && pos.y < 3.6) {
            if ((t2 = TOTORO_LEYE) < t) {
                t = t2;
                obj = EYE;
            }
            if ((t2 = TOTORO_REYE) < t) {
                t = t2;
                obj = EYE;
            }
            if ((t2 = TOTORO_LPUPIL) < t) {
                t = t2;
                obj = FACE_DETAIL;
            } 
            if ((t2 = TOTORO_RPUPIL) < t) {
                t = t2;
                obj = FACE_DETAIL;
            }
            if ((t2 = TOTORO_NOSE) < t) {
                t = t2;
                obj = FACE_DETAIL;
            }
        }
    }
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeNormal(vec3 pos, vec3 lightPos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                            sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                            sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}


vec2 random1(vec2 p) {
    return fract(sin(vec2(dot(p, vec2(127.1, 311.7)),
                 dot(p, vec2(269.5,183.3))))
                 * 43758.5453);
}

vec2 random2(vec2 p) {
    return fract(sin(vec2(dot(p, vec2(324.1, 21.7)),
                 dot(p, vec2(45.5,234.3))))
                 * 345.098);
}

float WorleyNoise1(vec2 uv) {
    const float dimen = 10.0; // dimension
    uv[0] *= dimen;
    float ratio = iResolution.y / iResolution.x;
    uv[1] *= dimen * ratio; // Now the space is 10x10 instead of 1x1. Change this to any number you want.
    vec2 uvInt = floor(uv);
    vec2 uvFract = fract(uv);
    float minDist = 1.0; // Minimum distance initialized to max.

        for(int y = -1; y <= 1; ++y) {
            for(int x = -1; x <= 1; ++x) {
                vec2 neighbor = vec2(float(x), float(y)); // Direction in which neighbor cell lies
                vec2 point = random1(uvInt + neighbor); // Get the Voronoi centerpoint for the neighboring cell
                vec2 diff = neighbor + point - uvFract; // Distance between fragment coord and neighborâ€™s Voronoi point
                float dist = length(diff);
                minDist = min(minDist, dist);
            }
        }

    return minDist;
}

