vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 light, vec3 view) {
    float t;
    switch(hitObj) {

        case SIGN:
        vec3 red = vec3(0.8, 0.8, 0.7) * max(0.0, dot(n, light));
        // specular
        vec3 l = normalize(light - p);
        vec3 refl = reflect(-l, n);
        float spec = pow(dot(normalize(view - p), refl), 5.);
        red += 0.09 * 10. * spec;
        red = clamp(red, vec3(0., 0., 0.), vec3(1., 1., 1.));
        return red;
        break;

        case SIGN_POST:
        vec3 blue = vec3(0.5, 0.6, 1.0) * max(0.0, dot(n, light));
        if (p.y > 2.4 && p.y < 3.1) {
            blue = vec3(0.9, 0.9, 1.0) * max(0.0, dot(n, light));
        }
        return blue;
        break;

        case FACE_DETAIL:
        vec3 black = vec3(0.2, 0.2, 0.2) * max(0.0, dot(n, light));
        return black;

        case TOTORO:
        vec3 gray = vec3(0.6, 0.65, 0.525) * max(0.0, dot(n, light));
        gray += 0.2 * vec3(0.45, 0.55, 0.425);
        return gray;
        break;

        case TOTORO_FUR:
        vec3 yellow = vec3(1., 0.95, 0.725) * max(0.0, dot(n, light));
        yellow += vec3(1., 1., 0.225) * 0.25;
        return yellow;
        break;

        // better view of this noise function here: https://www.shadertoy.com/view/wslBW2
        case FLOOR:
        vec3 col = vec3(0.8, 0.8, 0.8) * max(0.0, dot(n, light));
        float noise1 = WorleyNoise1(p.xz);
        float noise2 = WorleyNoise2(p.xz);
        float noise3 = WorleyNoise2(p.xz);
        float diam1 = 0.1 * (sin((iTime + 9.) * 0.5) / cos((iTime + 9.) * 0.5));
        float diam2 = 0.1 * (sin(iTime * 0.5) / cos(iTime * 0.5));
        float diam3 = 0.1 * (sin((iTime + 4.) * 0.5) / cos((iTime + 4.) * 0.5));
        float diam4 = 0.1 * (sin((iTime - 5.) * 0.5) / cos((iTime - 5.) * 0.5));
        float diam5 = 0.1 * (sin((iTime + 2.) * 0.5) / cos((iTime + 2.) * 0.5));
    	float diam6 = 0.1 * (sin(iTime - 9. * 0.5) / cos(iTime - 9. * 0.5));

        if (noise1 >= diam1 && noise1 <= diam1 + 0.016) {
            col += vec3(0.7);
        } else {
            col += vec3(0.);
        }

        if (noise1 >= diam2 && noise1 <= diam2 + 0.015) {
            col += vec3(0.7);
        } else {
            col += vec3(0.);
        }

        if (noise2 >= diam3 && noise2 <= diam3 + 0.013) {
            col += vec3(0.7);
        } else {
            col += vec3(0.);
        }

        if (noise2 >= diam4 && noise2 <= diam4 + 0.014) {
            col += vec3(0.7);
        } else {
            col += vec3(0.);
        }

        if (noise3 >= diam5 && noise3 <= diam5 + 0.01) {
            col += vec3(0.7);
        } else {
            col += vec3(0.);
        }
        if (noise3 >= diam6 && noise3 <= diam6 + 0.01) {
            col += vec3(0.7);
        } else {
            col += vec3(0.);
        }

        return col;
        break;

        case EYE:
        vec3 pureWhite = vec3(1.0, 1.0, 1.0);
        return pureWhite;
        break;

        case -1:
        return vec3(0.0);
        break;
    }
    return vec3(1.0);
}

float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - sceneMap3D(p + n * i * AO_DIST));
    }
    return 1.0 - k * aoSum;
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    float ao = fiveTapAO(isect + vec3(1.5, 0., 0.), nor, FIVETAP_K);

    if (hitObj == FLOOR) {
        surfaceColor *= ao;
    }

    return Intersection(t, surfaceColor, isect, hitObj);
}

// from https://www.shadertoy.com/view/XdfGDH
float normpdf(in float x, in float sigma)
{
	return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    Intersection aaIsects[4];
    vec3 dir, eye, ref;
    int idx = 0;
    for(float i = 0.0; i < 1.0; i += 0.5) {
        for(float j = 0.0; j < 1.0; j += 0.5) {
            raycast(uv2 + vec2(i, j) / iResolution.xy, dir, eye, ref);
            aaIsects[idx++] = sdf3D(dir, eye, LIGHT_POS);
        }
    }

    if (aaIsects[0].object != -1) {

        vec3 avgColor = vec3(0.0);
        for(int i = 0; i < 4; ++i) {
            avgColor += aaIsects[i].color;
        }
        avgColor *= 0.25;
        fragColor = vec4(avgColor * step(0.0, aaIsects[0].t), 1.0);
        float fogT = smoothstep(30.0, 50.0, distance(aaIsects[0].p, eye));
        fragColor.rgb = mix(fragColor.rgb, vec3(0.0), fogT);


    } else {

        // from https://www.shadertoy.com/view/XdfGDH
        const int mSize = 11;
        const int kSize = (mSize-1)/2;
        float kernel[mSize];
        vec3 final_color = vec3(0.0);

        //create the 1-D kernel
        float sigma = 20.0;
        float Z = 0.0;
        for (int j = 0; j <= kSize; ++j) {
            kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), sigma);
        }

        //get the normalization factor (as the gaussian has been clamped)
        for (int j = 0; j < mSize; ++j) {
            Z += kernel[j];
        }

        //read out the texels
        for (int i= -kSize; i <= kSize; ++i) {
            for (int j=-kSize; j <= kSize; ++j) {
                final_color += kernel[kSize+j] * kernel[kSize+i] * 
                    texture(iChannel0, (fragCoord.xy + vec2(float(i),float(j))) / iResolution.xy).rgb;
            }
        }

        vec2 uv = fragCoord/iResolution.xy;
        vec2 uv2 = 2.0 * uv - vec2(1.0);
        vec4 unblurredColor = texture(iChannel0, uv);
        fragColor = unblurredColor;
        fragColor = vec4(mix(unblurredColor.rgb, final_color/(Z*Z), unblurredColor.a), 1.0);

    }        
}
