void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float redNoise = fract( sin(uv.x*100.0*iTime + uv.y*iTime*600.0)*5000.0 );
    float greenNoise = fract( sin(uv.x*200.0*iTime + uv.y*iTime*500.0)*5000.0 );
    float blueNoise = fract( sin(uv.x*300.0*iTime + uv.y*iTime*400.0)*5000.0 );
    // Time varying pixel color
    vec3 col =vec3(redNoise, greenNoise, blueNoise);

    // Output to screen
    fragColor = vec4(col,1.0);
}
