#define AA 2
#define ZERO (min(iFrame,0))



float dot2(in vec2 v ) { return dot(v,v); }


mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(vec2(c, -s),vec2( s, c));
}
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float comp(float a,float b){

    return (a < b) ? a : b;
}
float opSmoothUnion( float d1, float d2, float k )
{
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
	//float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
	//return mix( d2, d1, h ) - k*h*(1.0-h);
}
float opSmoothSubtraction( float d1, float d2, float k )
{
    float h = max(k-abs(-d1-d2),0.0);
    return max(-d1, d2) + h*h*0.25/k;
	//float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
	//return mix( d2, -d1, h ) + k*h*(1.0-h);
}

float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}
vec3 scene(vec2 p){
    vec3 col=vec3(1.0,0.5,0.1);


    float header=sdBox(p+vec2(0.0,-0.85),vec2(3.0,0.15));
	float footer=sdBox(p+vec2(0.0,0.85),vec2(3.0,0.15));
    float esquerda=sdBox(p+vec2(1.4,0.0),vec2(0.4,0.7));





   if(header < 0.0) col=vec3(0.0);

   if(footer < 0.0) col=vec3(1.0,0.0,0.0);

   if(esquerda < 0.0) col=vec3(1.0);
   return col;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.0);

    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
     // pixel coordinates
      vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
     vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;   
 	 vec3 col=vec3(0.0);

  	  col *= 1.5 - 0.2*length(p);
      col = col*vec3(1.11,0.89,0.79);

      col=scene(p);

         tot += col;
    }

    tot /= float(AA*AA);
  fragColor = vec4(tot,1.0);
}
