vec4 circle(vec2 xy, vec2 pos, float r, vec4 c) {
	float p = abs(distance(xy*iResolution.xy, pos*iResolution.xy));

    return vec4(step(p, r)) * c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 xy = fragCoord/iResolution.xy;
    vec2 mid = vec2(0.5);
    vec2 mos = (iMouse.z > .0)? iMouse.xy/iResolution.xy : mid;


    float r = 50.;

    float diff = abs(distance(mid, mos));
    vec2 off = mix(mid, mos, diff);

    vec4 blue = vec4(0, 0.4, 1, 1);
    vec4 green = vec4(0, 1, 0.4, 1);

    vec4 px = circle(xy,  off, r, blue);

    px += circle(xy, 
                 off+vec2(sin(iTime/.5)/2., mix(0., -0.1, abs(sin(iTime)))), 
                 mix(20., 60., abs(sin(iTime))), 
                 green * abs(sin(iTime)));

    if (px == vec4(0)) {
    	px = texture(iChannel0, xy + off);
    }

    fragColor = px;
}
