void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 crd = 0.005* (fragCoord.xy - iResolution.xy / 2.0);
        float len;

    for (int i = 0; i < 10; i++) {
        len = length(vec2(crd.x, crd.y));
        crd.x = crd.x - cos(crd.y + sin(len)) + cos(iTime / 2.92);
        crd.y = crd.y + sin(crd.x + cos(len)) + sin(iTime / 2.90);
    }
    //color
    fragColor =  vec4(cos(len * -0.8), cos(len * 0.164), cos(len * 0.1), 1.0);

    //bw
    //fragColor =  vec4(cos(len), cos(len), cos(len), 1.0);
}
