//https://www.youtube.com/watch?v=6IWXkV82oyY

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 m = iMouse.xy/iResolution.xy;
    //float zoom = pow(10.0, -m.x*3.0);
    float zoom = pow(10.0, -(abs( sin(iTime * 0.1)))*6.0);		// 6.0 - max zoom, may be inc

    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy)/iResolution.y;

    vec2 c = uv * zoom * 3.0;
    //c+= vec2(-0.69955, 0.37999);
    c+= vec2(-0.9195, 0.27999);

    vec2 z= vec2(0.0);
    float iter = 0.0;

    const float max_iter = 100.0;

    for (float i=0.0; i<max_iter; i++){
     	z = vec2(z.x*z.x - z.y*z.y, 2.0* z.x *z.y) + c;

        if (length(z)> 2.0) break;

        iter++;
    }

    float f = iter/max_iter;

    vec3 col = vec3(f);
    fragColor = vec4(col,1.0);
}
