void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0,uv).xyz;

    fragColor = vec4(col,1.0);
}

vec2 rotate(vec2 v, float a)
{
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uvOffset = vec2(uv.x + sin(iTime*0.3), uv.y + sin(iTime*.23));

    vec3 tex0 = texture( iChannel0, rotate(uvOffset-0.5,sin(iTime*0.75))).xyz;
    vec3 tex1 = texture( iChannel1, uv).xyz;

    vec3 col = mix(tex0,tex1,0.96);

    fragColor = vec4(col,1.0);
}
