void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //	Constants
    float thickness = 0.01;

    //	Map coords to {x, 
    vec2 uv = fragCoord/iResolution.xy;

    //	Black background
    fragColor = vec4(0.0);

    float a = texture(iChannel0, vec2(uv.x, 0.0)).r;
    if(abs(uv.y - (texture(iChannel0, vec2(uv.x, 1.0)).r * a + 0.1)) < thickness){
        fragColor = vec4(1.0);
    }
}
