void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord / iResolution.xy;

	float l1 = 1.- smoothstep(uv.x+sin(uv.y*5.+iTime+2./2.0)/20.-0.31,0.5,0.501);
	float l2 = 1.- smoothstep(uv.x+sin(uv.y*5.+iTime+3./2.5)/20.-0.32,0.5,0.501);
	float l3 = 1.- smoothstep(uv.x+sin(uv.y*5.+iTime   /3. )/20.-0.32,0.5,0.501);


  float l = 1.- (l1 + l2 + l3);

	fragColor = vec4(l,l,l, 1.0);//1.2;
}
