void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0,uv);
}

void mainImage( out vec4 fragColor, in vec2 pos )
{
    vec2 res = iResolution.xy;
    fragColor = texture(iChannel0,(pos-vec2(0,0))/res)/1.01;
    if(texture(iChannel0,(pos-vec2(0,1.))/res).x>texture(iChannel0,(pos-vec2(0))/res).x){
    	fragColor = texture(iChannel0,(pos-vec2(0,1.))/res)/1.01;
    }
    fragColor+=texture(iChannel1,pos/res+iTime/100.+vec2(sin(iTime),cos(iTime))/50.)/100.;
}
