void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float dist = distance(uv, vec2(0.5, 0.5));
    float brightness = 1. / (dist);


    // Time varying pixel color
    vec3 col = vec3(sin(iTime) * 3., 0., brightness);

    uv *= vec2(sin(iTime * 34.) * .3, cos(iTime * 199.) * .4);

	vec4 texcol = texture(iChannel0, uv + iTime);
    vec4 texcol1 = texture(iChannel0, uv + vec2(sin(iTime), 0.));
    vec4 texcol2 = texture(iChannel0, uv + vec2(0., cos(iTime)));

    // Output to screen
    fragColor = (texcol.r * texcol1.r * texcol2.r) * vec4(col,1.0);
}
