void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //scaling
    vec2 sp  = (fragCoord*2.-iResolution.xy)/iResolution.y;

    //inverted worley noise
    vec3 col = vec3(1.-worleynoise(vec3(sp.x*5.,iTime*1.,sp.y*5.)));

    //gamma correction
    fragColor = vec4(pow(col,vec3(1./2.2)),1);
}

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

float worleynoise(vec3 p){
    vec3 n = floor( p );
    vec3 f = fract( p );

	float dis = 1.;
    for( int k=-1; k<=1; k++ )
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {	
        vec3  g = vec3(i,j,k);
      	vec3  o = hash33( n + g );
        vec3  delta = g+o-f;
        float d = length(delta);
      	dis = min(dis,d);

    }

    return dis;
}
