renderpass.code:
mat2 Rot(float a) {
	float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	float aa = 1.5 / iResolution.y;

    // deformation
    uv *= 2.;
    uv *= Rot(iTime*.09);
    uv.x += atan(0.07, uv.y*uv.y*(sin(iTime*.5)*.2+.2))*.4;
    uv /= atan(4., iTime*4.)+2.;
    uv *= Rot(iTime*.2);
    uv.x *= uv.x;

    float d = length(uv+.14);
    vec3 col = vec3(.2, .0, .4);

    //circles
    float c1 = smoothstep(.2, .2+aa, d);
    float c2 = smoothstep(.1, .1+aa, d);
    float circle = mix(c1, c2, .87);
    col.rb += circle*.9;

    c1 = smoothstep(.3, .3+aa, d);
    c2 = smoothstep(.4, .4+aa, d);
    circle = mix(c1, c2, .5);
    col.r += circle*.1;

    c1 = smoothstep(.5, .5+aa, d);
    c2 = smoothstep(.6, .6+aa, d);
    circle = mix(c1, c2, .5);
    col.rg -= circle;

    c1 = smoothstep(.8, .8+aa, d);
    c2 = smoothstep(1., 1.+aa, d);
    circle = mix(c1, c2, .5);
    col.r += circle;

    c1 = smoothstep(1.2, 1.2+aa, d);
    c2 = smoothstep(1.4, 1.4+aa, d);
    circle = mix(c1, c2, .5);
    col.g -= circle;

    c1 = smoothstep(1.8, 1.8+aa, d);
    c2 = smoothstep(2.5, 2.5+aa, d);
    circle = mix(c1, c2, .5);
    col.r += circle;

    // color adjustment
	col.r = col.b - col.r *.8;
    col.b *= .13;
    col.g *= 1.2;
    col += .04;

    fragColor = vec4(col,1.0);
}
