# define PI 3.141592653589793
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.y;
    vec2 u = fract(uv.xy*5.)-0.5;
    float z = 5.;
    vec2 g = iTime*.02 + floor(uv * z);
    float b =  rand(floor(g + 3.321))*3.;
    float r =.7;
    float angle = PI*0.25+floor(rand(floor(g))*4.)*PI*0.5;      
    u.x+=sin(angle)*r;
    u.y+=cos(angle)*r;
    float d = length(u*.3);
 //	float k = smoothstep(d,d*1.01,0.5);
    float k = fract(d*2.0-iTime*(b-0.5)*0.2);
    vec3 cc = mix( vec3(.1, 0.1, 0.2), vec3(1.0, 0.14, 0), k);
    fragColor = vec4(cc,1.0);
}
