#define PI 3.1415927

float cir(vec2 p, float r)
{
    return smoothstep(9./iResolution.y,0.,length(p)-r);
}

void mainImage( out vec4 c, in vec2 f )
{
    vec2 p = 2.*((2.*f-iResolution.xy)/iResolution.y), c1, c2; c.rgb *= 0.0;
    float t = iTime*2.+atan(p.x,p.y)-atan(p.y,p.x), r = .5, o = .25, b=cos(t)*r, z= sin(t)*r;
    for(float x=-1.; x<=1.; x++)
        for(float y=-1.;y<=1.;y++)
        {
            c1 = (p-vec2(x+o,y+o))+vec2(z,b);
            c2 = (p-vec2(x-o,y-o))-vec2(z,b);
            c.rgb += vec3(cir(c2,.1)+cir(c1,.1));
         }
}
