#define RADIUS 1.0   

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // 1. layout UV from [0..1] to [-1..1]
	vec2 uv = (2.* fragCoord - iResolution.xy) / iResolution.y;

    // 2. draw anti-aliased circle
    float d = length(uv);
    float wd = d * 3./iResolution.y; // <=> float wd = fwidth(d);
    float circle = smoothstep(RADIUS + wd, RADIUS - wd, d);

    // 3. result
    fragColor = vec4(vec3(circle), 1.0);
}
