#define PI 3.1415926535

float N11(float n)
{
    vec2 v1 = vec2(fract(n*129.3484),fract(n*10298.2827));
    vec2 v2 = vec2(492.23,n);
    return fract(dot(v1,v2));
}

vec2 N12(float n)
{
    vec2 v1 = vec2(fract(n*33.24102),fract(n*304.70234556));
    vec2 v2 = vec2(39.3823,n);
    return vec2(fract(dot(v1,v2)),fract(405.29*v2.y/v1.y));
}

vec2 N22(vec2 p)
{
    return vec2(N11(7.238+p.y+p.x*0.62874),N11(3.4148*p.y-8.29*p.x+1.39558));
}

float perlin(vec2 p,float scale,float seed)
{
    vec2 pS = p*scale;

    float X1 = floor(p.x*scale);
    float X2 = X1+1.;
    float Y1 = floor(p.y*scale);
    float Y2 = Y1+1.;

    vec2 v11 = vec2(X1,Y1);

    vec2 gp = pS - v11;
    float xCub = pow(gp.x,3.);
    float yCub = pow(gp.y,3.);
    gp = vec2((6.*gp.x*gp.x-15.*gp.x+10.)*xCub,
              (6.*gp.y*gp.y-15.*gp.y+10.)*yCub);

    vec2 v12 = vec2(X1,Y2);
    vec2 v21 = vec2(X2,Y1);
    vec2 v22 = vec2(X2,Y2);


    vec2 g11 = N22(v11*(seed))*2.-1.;
    vec2 g12 = N22(v12*(seed))*2.-1.;
    vec2 g21 = N22(v21*(seed))*2.-1.;
    vec2 g22 = N22(v22*(seed))*2.-1.;

    vec2 contribY1 = mix(g12,g22,gp.x);
    vec2 contribY2 = mix(g11,g21,gp.x);

    vec2 contrib = mix(contribY2,contribY1,gp.y);
    float value= sin((15.+.5*sin(iTime))*length(contrib)+iTime*.3);

    return smoothstep(0.,fwidth(value),value);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy*2.)-1.;
    uv.x *= iResolution.x/iResolution.y;

    float rectTime = iTime*.3;
    float valSin = sin(rectTime*PI)*5.;

    vec3 col1base = vec3(.97,.31,.63);
    vec3 col2base = vec3(.98,.97,.48);

    float seed1 = 1.5948+floor(rectTime);
    float seed2 = 2.1918+floor(rectTime*.5-.5*rectTime/iTime);

    float rotAngle1 = N11(seed1)*PI*.5;
    mat3 rotMat1 = mat3(1,0,0,
                       0,cos(rotAngle1),sin(rotAngle1),
                       0,-sin(rotAngle1),cos(rotAngle1));

   	float rotAngle2 = N11(seed2)*PI*.5;
    mat3 rotMat2 = mat3(cos(rotAngle2),0,sin(rotAngle2),
                       0,1,0,
                       -sin(rotAngle2),0,cos(rotAngle2));


    vec3 col1 = rotMat1*col1base;
    vec3 col2 = rotMat1*col2base;

    float scale1 = 2.+2.*N11(seed1);

    vec3 val1 = mix(col1,col2,perlin(uv,scale1,seed1));

    vec3 col3 = rotMat2*col1base.zyx;
    vec3 col4 = rotMat2*col2base.zyx;


    float scale2 = 2.+2.*N11(seed2);

    vec3 val2 = mix(col3,col4,perlin(uv,scale2,seed2));

    float d = smoothstep(valSin,valSin+fwidth(length(uv)),length(uv));



    vec3 col = mix(val1,val2,d);

    // Output to screen
    fragColor = vec4(col,1.0);
}
