// === smooth floor (e.g. for antialiasing)  ref URL: https://www.shadertoy.com/view/tsyXzV

// pix = pixel width. You can use fwidth(x) if no discontinuity in x.

#  define sfloor(x) ( floor(x-pix/2.) + max( 0., 1.-fract(-x+pix/2.) / pix ) ) 
#  define sfract(x) ( x - sfloor(x) )

// --- simpler version with ramp on left sides rather than symmetrical : 
//#define sfloor(x) ( floor(x) + max( 0., 1.-fract(-x) / pix ) )  

// --- variant: replace max(0., by smoothstep(0.,1.5, 


void mainImage( out vec4 O, vec2 u ) {
    vec2 R = iResolution.xy,
         U =     u / R.y;
    float pix = 1. / R.y,    // pixel width 
         zoom = 4.;          // to zoom in pixels
    O -= O;

    U.x *= 10.; pix = fwidth(U.x); // scale graphics
    // draw curves
    O += max(0., 1.- abs (U.y-.5 - .1* floor(U.x/zoom) ) * zoom/pix );
    O += max(0., 1.- abs (U.y-.3 - .1*sfloor(U.x/zoom) ) * zoom/pix );
    // draw greyscales
    if ( abs(U.y-.2 ) < .05 ) O +=  floor(U.x/zoom)/4.;
    if ( abs(U.y-.07) < .05 ) O += sfloor(U.x/zoom)/4.;


}
