void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 nuv = uv - vec2(.5, .5);
    nuv *= 10.+(iMouse.x/iResolution.x)*10.+sin(iTime);

    float val = smoothstep(0.5, 1.5, abs(sin(nuv.x+sin(nuv.x*nuv.y-iTime))));

    vec3 col = val * vec3((.5+sin(iTime)*.5)*abs(nuv.x), (.5+cos(iTime)*.5)*abs(nuv.x), .5-sin(iTime)*.5);

    float vig = smoothstep(0., 0.1, uv.x*(1.-uv.x)*uv.y*(1.-uv.y));
    float vigstr = mix(1., vig, iMouse.y/iResolution.y);

    fragColor = vec4(vigstr*col,1.0);
}
